-- ----------------------------
-- Table structure for xinfei_article
-- ----------------------------
DROP TABLE IF EXISTS `xinfei_article`;
CREATE TABLE `xinfei_article`  (
  `article_id` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章ID',
  `article_title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `article_date` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '发布日期',
  `article_html` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章内容',
  `article_hot` int(11) NOT NULL DEFAULT 1 COMMENT '热度值',
  `article_user` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '发布者',
  `article_display` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '是否显示',
  `article_category` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章类别',
  `article_text` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '没有任何样式',
  PRIMARY KEY (`article_id`) USING BTREE,
  UNIQUE INDEX `article_id`(`article_id`) USING BTREE COMMENT 'id索引'
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

